mapper.select:
select user_id, user_name 
from user 
where user_id=#{user_id} and user_pwd=#{user_pwd}

mapper.update:
update user 
set user_phone_num=#{user_phone_num}, user_email=#{user_email} 
where user_id =#{user_id}

update user 
set user_pwd=#{user_pwd2}  
where user_id=#{user_id} and user_pwd=#{user_pwd}

